function selectedSubjectName() {
    var subjectIdNode = document.getElementById('asset-list');
    var value =
    subjectIdNode.options[subjectIdNode.selectedIndex].text;
    console.log(value);
}

const AssetDropDown =() => {

useFetch(`http://localhost:3000/allunits`, 'asset-list');

    return(
        <div className='combo-box-container'  >
        
            {/* <input  
                className = 'combo-box'
                type="text" 
                list="asset-list"
                /> */}
            <select id="asset-list" className = 'combo-box' onChange={() => {selectedSubjectName()}}>
                <option text=' '/>
            </select>
            
        </div>
    )
}

export default AssetDropDown;
