import React, {useEffect} from 'react'
import './drop-down.styles.scss'
import { createStructuredSelector } from 'reselect';

// import useDropDownFetch from '../../effects/use-fetch-drop-down.effect';

import { connect } from "react-redux";
import { requestSelectedAssetData } from "../../redux/asset/asset.actions";
import { requestUserDropDownOptions } from '../../redux/drop-downs/drop-down.actions'

import  selectedListItemID  from "./drop-down.utils";
import { selectUsers } from "../../redux/drop-downs/drop-down.selectors";

const AssetDropDown =({ getAssetDetails, getUserOptions, users }) => {

    useEffect(() => {
        // Get Data from /allusers.
        getUserOptions()
      }, [getUserOptions]);
    
    return(
        <div className='combo-box-container' >
            <select id="asset-list" className = 'combo-box' onChange={getAssetDetails} >
                <option text=' '/>
                {   
                    users ? (
                        users.map(user => (
                    <option key={ user.id }> { user.id } </option> 
                    ))
                    ):(
                    <option></option>
                    )
                }
            </select>
        </div>
    )
}

const mapStateToProps = createStructuredSelector({
    users: selectUsers
})

const mapDispatchToProps = (dispatch) => ({
    getAssetDetails: () => { dispatch(requestSelectedAssetData(selectedListItemID('asset-list')))},
    getUserOptions: () => { dispatch(requestUserDropDownOptions())}
})

export default connect(mapStateToProps, mapDispatchToProps)(AssetDropDown);
