import React, { Component } from "react";
import './popup.css'
export default class UnitsInUse extends Component {
  handleClick = () => {
   this.props.toggle();
  };
  constructor(props) {
    super(props);
    this.state = {
        unitsInUse: []
    }
  }

  componentDidMount() {
    fetch('http://localhost:3000/unitsinuse', {
        method: 'get',
        headers: {'Content-Type': 'application/json'},
    })
    .then(data => data.json())
    .then (data =>  this.setState({unitsInUse:data}))   
}

render() {  
  return (
   <div className="modal_main">
     <div className="modal_report_content">
        <span className="close" onClick={this.handleClick}>&times;    </span>
            <div className='container'>
                <h1>Units in Use</h1>
                <div className=" pb4 ">
                    <div className="overflow-auto">
                        <table className="f6 w-100 mw8 tc " cellSpacing="0">
                        <thead>
                            <tr className="stripe-dark" >
                                <th className="fw6  pa3 bg-white">Unit ID</th>
                                <th className="fw6  pa3 bg-white">Unit Name</th>
                                <th className="fw6  pa3 bg-white">Model</th>
                                <th className="fw6  pa3 bg-white">Serial</th> 
                                <th className="fw6  pa3 bg-white">Status</th> 
                            </tr>
                        </thead>
                        <tbody className="lh-copy">
                        {this.state.unitsInUse.map((unit =>
                            <tr key={unit.id} className="stripe-dark">
                                <td >{unit.id}</td>
                                <td >{unit.name}</td>
                                <td >{unit.model}</td>
                                <td >{unit.serial}</td>
                                <td >{unit.status}</td>
                            </tr>
                            ))} 
                        </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
   </div>
  );
 }
}
