import React, { useState } from "react";
import { connect } from "react-redux";

import { addNewUser } from "../../redux/user/user.actions";
import { toggleAddUser } from "../../redux/pop-ups/pop-ups.actions";

import './popup.scss' 

const AddUser = ( {createNewUser, toggleAddUser} ) =>{


  let [userID, setUserID] = useState('')
  let [fname, setFname] = useState('')
  let [lname, setLname] = useState('')

  const onUseridChange = (event) => {
    setUserID(event.target.value) 
  } ;

  const onFnameChange = (event) => {
    setFname(event.target.value) 
  };

 const onLnameChange = (event) => {
    setLname(event.target.value) 
  };

  const inputReset = () => {
    document.getElementById('userid').value = ''
    document.getElementById('fname').value = ''
    document.getElementById('lname').value = ''
    document.getElementById('userid').focus()
    alert('User Created!')
  }
 
  return (
   <div className="modal_main">
      <div className="modal_content">
        <span className="close" 
        onClick={toggleAddUser}
        >&times;    </span>
          <div className='container'>
              <h1>Add User</h1>
              <article className="center">
                <div action="sign-up_submit" method="get" acceptCharset="utf-8">
                  <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                    <div>
                      <label className="db fw6 lh-copy" htmlFor="email-address">User ID</label>
                      <input 
                      onChange={onUseridChange} 
                      className="pa2 input-reset ba" type="text" name="userid" id="userid"/>
                    </div>
                    <div className="mt3">
                      <label className="db fw6 lh-copy" htmlFor="fname">First Name</label>
                      <input 
                      onChange={onFnameChange} 
                      className="pa2 input-reset ba " type="text" name="fname"  id="fname" />
                    </div>
                    <div className="mt3">
                      <label className="db fw6 lh-copy" htmlFor="lname">Last Name</label>
                      <input 
                      onChange={onLnameChange} 
                      className="pa2 input-reset ba " type="text" name="lname"  id="lname"/>
                    </div>
                  </fieldset>
                  <div className="mt3">
                  {/* onClick={() => { func1(); func2();}} */}
                    <input  
                      onClick={() => {createNewUser(userID, fname, lname); inputReset();}}
                      className="b ph3 pv2 input-reset ba b--black grow pointer f6" 
                      type = "submit"
                      defaultValue="Submit"
                    />
                    </div>
                </div>
              </article>
          </div>
      </div>
   </div>
  );
}


const mapDispatchToProps = (dispatch) => ({
  createNewUser: (userId, fname, lname) => { dispatch(addNewUser(userId, fname, lname)) },
  toggleAddUser: () => dispatch(toggleAddUser())
})

export default connect(null, mapDispatchToProps)(AddUser);
