import React from 'react'
import './register.styles.scss'

class RegistrationPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            fname: '',
            lname: '',
            email: '',
            password: ''
        }
    }

    
    onFirstNameChange = (event) => {
        this.setState({fname: event.target.value})
    }

    onLastNameChange = (event) => {
        this.setState({lname: event.target.value})
    }


    onEmailChange = (event) => {
        this.setState({email: event.target.value})
    }

    onPasswordChange = (event) => {
        this.setState({password: event.target.value})
    }

    onSubmitRegister = () => {
        fetch('http://localhost:3000/register', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                fname: this.state.fname,
                lname: this.state.lname,
                email: this.state.email,
                password: this.state.password
            })
        })
        // * Handling incorrect credentials
        .then(response => response.json()) 
        .then(member => {
            if (member) {
                // * this.props means it is a function that is built in the APP.js component
                this.props.loadMember(member)
                this.props.onRouteChange('home')
            }
        })
    }



    render() {    
        return (
        <div className="registration-body">
            <article className="registration-container mv">
                <main className="registration-inner-container">
                    <div className="measure">
                        <fieldset id="sign_up" className="registration-fieldset">
                        <legend className="registration-legend">Register</legend>    
                        <div className="name-field-container">
                            <div className='name-field'>   
                                <label className="registration-label" htmlFor="name">First Name</label>
                                <input className="registration-input" 
                                    type="text" 
                                    name="fname"  
                                    id="fname"
                                    onChange ={this.onFirstNameChange }
                                    />
                            </div>

                            <div className='name-field'>   
                                <label className="registration-label lname" htmlFor="name">Last Name</label>
                                <input className="registration-input" 
                                    type="text" 
                                    name="lname"  
                                    id="lname"
                                    onChange ={this.onLastNameChange }
                                />
                            </div>
                        </div>


                        <div className="mt3">
                            <label className="registration-label" htmlFor="email-address">Email</label>
                            <input className="registration-input" 
                                type="email" 
                                name="email-address"  
                                id="email-address"
                                onChange = {this.onEmailChange}
                            />
                        </div>

                        <div className="mv3">
                            <label className="registration-label" htmlFor="password">Password</label>
                            <input className="registration-input" 
                                type="password" 
                                name="password"  
                                id="password"
                                onChange ={this.onPasswordChange}
                            />
                        </div>
                        
                        </fieldset>
                        <div className="">
                        <input className="registration-submit" 
                            type="submit" 
                            value="Register"
                            onClick = {this.onSubmitRegister}
                            />
                        </div>
                    </div>
                </main>
            </article>
        </div>
    )}
}

export default RegistrationPage
