import styled from 'styled-components';

export const SignInPageBody = styled.div`
    background-color:red;
    height:100vh;
    display:flex;
    flex-direction: column;
`;

export const SignInLegend = styled.legend`
    color: #4178BE;
    font-size: 3.5rem;
    text-align: center;
`;


export const SignInArticle = styled.article`
background-color:green;
    text-align: center;
    border-radius: 0.5rem;
    border-style: solid;
    border-width: 1px;
    border-color: rgba(0, 0, 0, 0.1);
    width: 25%;
    height: 40%;
    box-shadow: 4px 4px 8px 0 rgba(0, 0, 0, 0.2);
    position: relative;
    margin-right: auto;
    margin-left: auto;
`;

export const SignInMain = styled.main`
    padding: 2rem;
    color: rgba(0, 0, 0, 0.8);
`;

export const SignInFieldSet = styled.fieldset`
    border-style: solid;
    border-width: 1px;
    border-color: transparent;
    padding-left: 0;
    padding-right: 0;
    margin-left: 0;
    margin-right: 0;
`;

export const SignInInput = styled.input`
    padding: 0.5rem;
    -webkit-appearance: none;
    -moz-appearance: none;
    border:none;
    border-bottom: 1px solid lightgrey;
    .hover-bg-black:hover {
      background-color: #000;
    }
    .hover-white:hover {
      color: #fff;
    }
    width: 100%;
`;

export const SignInSubmit = styled.input`
    color: #4178BE; 
    font-weight: bold;
    padding:.5rem 1rem;
    -webkit-appearance: none;
    -moz-appearance: none;
    border: 3px solid #4178BE;
    background-color: transparent;
    -moz-osx-font-smoothing: grayscale;
    -webkit-backface-visibility: hidden;
    backface-visibility: hidden;
    -webkit-transform: translateZ(0);
    transform: translateZ(0);
    transition: -webkit-transform 0.25s ease-out;
    transition: transform 0.25s ease-out;
    transition: transform 0.25s ease-out, -webkit-transform 0.25s ease-out;
    cursor: pointer;
    font-size: 0.875rem;
    display: inline-block;
    text-align: center;
`;

export const RegisterContainer = styled.div`
    line-height: 1.5;
    margin-top: 1rem;
`;

export const RegisterLink = styled.p`
    font-size: 0.875rem;
    text-decoration: none;
    transition: color 0.15s ease-in;
    opacity: 1;
    transition: opacity 0.15s ease-in;
    color: #000;
    display: block;
    cursor: pointer;
`;

export const Email = styled.div`
    display: flex;
`;

export const Password = styled.div`
    display: flex;
    margin-top: 1.5rem;
    margin-bottom: 1rem;
`;
