export const REQUEST_DROP_DOWN_OPTIONS = "REQUEST_DROP_DOWN_OPTIONS";
export const RECEIVE_DROP_DOWN_OPTIONS = "RECEIVE_DROP_DOWN_OPTIONS";
export const ADD_ITEM = "ADD_ITEM";

export const requestSelectedAssetData = (assetID) => ({ 
    type: REQUEST_DROP_DOWN_OPTIONS, assetID
});

export const receiveSelectedAssetData = (assetData) => ({
     type: RECEIVE_DROP_DOWN_OPTIONS, assetData
});

const addAssetToDropDown = asset => ({
    type: ADD_ITEM,
    payload: asset
});
