import { call, put, fork, take, takeLatest, all } from "redux-saga/effects";

import { 
    REQUEST_SELECTED_ASSET,
    CHECK_IN_SELECTED_ASSET_START,
    receiveSelectedAssetData,
    checkInSelectedAssetSuccess
   } from "../asset/asset.actions";

import { 
  fetchSelectedAssetData,
  checkInAsset
 } from "../../services/api";


 function* takeFirst(pattern, saga, ...args) {
  const task = yield fork(function* () {
      while(true) {
        const action = yield take(pattern);
        yield call(saga, ...args.concat(action));
      }
    });
    return task;
  }

// worker Saga: will be fired on USER_FETCH_REQUESTED actions
function* getRequestedSelectedAssetData(action) {
  try {
    // do api call
    const assetData = yield call(fetchSelectedAssetData, action.assetID);
    yield put(receiveSelectedAssetData(assetData));
  } catch (error) {
    console.log(error);
  }
}

function* postCheckInSelectedAsset(action) {
  try{
    console.log('THIS IS STEP 3333!!!!!')
    const assetData = yield call(checkInAsset, action.assetID )
    console.log('THIS IS STEP 8888!!!!!')
    yield put(checkInSelectedAssetSuccess(assetData))
    console.log('THIS IS STEP 10 10 10 10!!!!!')
  }  catch (error) {
    console.log(error)
  }
}

export  function* requestSelectedAssetSaga() {
  yield takeLatest(REQUEST_SELECTED_ASSET, getRequestedSelectedAssetData);
}

export  function* checkInSelectedAssetSaga() {
  console.log('THIS IS STEP 2222!!!!!')
  yield takeFirst(CHECK_IN_SELECTED_ASSET_START, postCheckInSelectedAsset);
  console.log('THIS IS STEP 9999!!!!!')
}

export function* assetSagas() {
    console.log('THIS IS STEP 1111!!!!!')
    yield all([call (requestSelectedAssetSaga), call(checkInSelectedAssetSaga)])
    yield console.log('THIS IS STEP 10101010!!!!!')
};
