import { 
    TOGGLE_MEMBER_SIGN_IN, 
    RECEIVE_MEMBER_DATA 
} from "./site-member.actions";

const INITIAL_STATE = { 
    memberSignedIn : false,
    memberID: null
};

const memberState = (state = INITIAL_STATE, { type, memberID }) => {
    switch (type) {
    case TOGGLE_MEMBER_SIGN_IN:
        return { 
            memberSignedIn : !state.memberSignedIn,
            memberID: memberID
        };
    case RECEIVE_MEMBER_DATA:
        return {
            ...state,
            memberData
        };
    default:
        return state 
    }
};

export default memberState;
