import React from 'react'
import './drop-down.styles.scss'

import useFetch from '../../effects/use-fetch.effect';


function selectedUserName() {
    var UserIdNode = document.getElementById('user-list');
    var value =
    UserIdNode.options[UserIdNode.selectedIndex].text;
    console.log(value);
};

const UserDropDown =() => {

useFetch(`http://localhost:3000/users`, 'user-list');

    return(
        <div className='combo-box-container'  >
            {/* <input  
                className = 'combo-box'
                type="text" 
                list="user-list"
                /> */}
            <select id="user-list" className = 'combo-box' onChange={ () => { selectedUserName() }}>
                <option text=' '/>
            </select>
        </div>
    )
}

export default UserDropDown;
