import React, {useEffect, useState} from "react";
import axios from "axios";
import { connect } from "react-redux";
import { toggleUnitsInUse } from "../../redux/pop-ups/pop-ups.actions";

import Loader from 'react-loader-spinner'
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css"

import { ModalMain, ModalReportContent, CloseButton, Header, 
        TableContainer, Table, TableHeading, TableRow, TableBody
  } from "./modal.styles";

const UnitsInUse = ({ toggleUnitsInUse, memberId }) => {

    const [data, setData] = useState();
 
    useEffect(() => {
        const fetchData = async () => {
          const result = await axios({
              method: 'post',
              url: 'http://localhost:3000/assetsinuse',
              data: {
                memberId: memberId
              }
            }
          );
     
          setData(result.data);
        };
     
        fetchData(memberId);
      }, [memberId]);

  return (
   <ModalMain>
        <ModalReportContent>
            <CloseButton onClick={toggleUnitsInUse}>&times;</CloseButton>
            <div className='container'>
            <Header>Units in Use</Header>
                <div className=" pb4 ">
                    <TableContainer>
                    {data ?
                        <Table cellSpacing="0">
                        <thead>
                            <TableRow >
                                <TableHeading>Unit ID</TableHeading>
                                <TableHeading>Unit Name</TableHeading>
                                <TableHeading>Model</TableHeading>
                                <TableHeading>Serial</TableHeading> 
                                <TableHeading>Status</TableHeading> 
                            </TableRow>
                        </thead>
                        <tbody className="lh-copy">
                            {data.map(unit => (
                                <tr key={unit.id} className="stripe-dark">
                                <td>{unit.id}</td>
                                <td >{unit.name}</td>
                                <td >{unit.model}</td>
                                <td >{unit.serial}</td>
                                <td >{ unit.status }</td>
                                </tr>
                            ))}
                        </tbody>
                        </Table>
                        : <Loader 
                            type="Puff"
                            color="#966eff"
                            height={70}
                            width={70}
                            className='loader'    
                            />}
                    </TableContainer>
                </div>
            </div>
        </ModalReportContent>
   </ModalMain>
  );
}

const mapStateToProps = (state) => ({
    memberId: state.memberState.memberId,
    seenUnitsInUse: state.popUpState.seenUnitsInUse
});

const mapDispatchToProps = dispatch => ({
    toggleUnitsInUse: () => dispatch(toggleUnitsInUse())
  });


export default connect(mapStateToProps, mapDispatchToProps)(UnitsInUse);
