import React, { useState } from "react";
import { useAlert } from 'react-alert'
import { connect } from "react-redux";

import { toggleAddUser } from "../../redux/pop-ups/pop-ups.actions";
import { requestUserDropDownOptions } from '../../redux/drop-downs/drop-down.actions'

import { createNewUser } from "../../services/api";

import './popup.scss' 

const AddUser = ( {toggleAddUser, getUserOptions} ) =>{

  const alert = useAlert()

  let [userID, setUserID] = useState('')
  let [fname, setFname] = useState('')
  let [lname, setLname] = useState('')

  const onUseridChange = (event) => {
    setUserID(event.target.value) 
  } ;

  const onFnameChange = (event) => {
    setFname(event.target.value) 
  };

 const onLnameChange = (event) => {
    setLname(event.target.value) 
  };

  const inputReset = () => {
    userID = null
    fname = null
    lname = null
    document.getElementById('userid').value = ''
    document.getElementById('fname').value = ''
    document.getElementById('lname').value = ''
    document.getElementById('userid').focus()
  };

  const submitUser = (event) => {
    if (userID && fname && lname){
      createNewUser(userID, fname, lname);
      alert.show(`${userID} added successfully.`, {type: 'success', position:'top center'})
      inputReset();
    } else {
      console.log(createNewUser(userID, fname, lname).[[PromiseResult]].message)
      alert.show('Please ensure that all fields are filled out and model is selected.', {type: 'error', position:'top center'})
    }
  };
  
  return (
   <div className="modal_main">
      <div className="modal_content">
        <span className="close" 
        onClick={() => {toggleAddUser(); getUserOptions();}}
        >&times;    </span>
          <div className='container'>
              <h1>Add User</h1>
              <article className="center">
                <div action="sign-up_submit" method="get" acceptCharset="utf-8">
                  <fieldset id="sign_up" className="form-container">
                    <div>
                      <label className="form-label" htmlFor="email-address">User ID</label>
                      <input 
                      onChange={onUseridChange} 
                      className="form-input" type="text" name="userid" id="userid"/>
                    </div>
                    <div className="mt3">
                      <label className="form-label" htmlFor="fname">First Name</label>
                      <input 
                      onChange={onFnameChange} 
                      className="form-input" type="text" name="fname"  id="fname" />
                    </div>
                    <div className="mt3">
                      <label className="form-label" htmlFor="lname">Last Name</label>
                      <input 
                      onChange={onLnameChange} 
                      className="form-input" type="text" name="lname"  id="lname"/>
                    </div>
                  </fieldset>
                  <div className="mt3">
                    <input  
                      onClick={() => submitUser()}
                      className="form-submit" 
                      type = "submit"
                      defaultValue="Submit"
                    />
                    </div>
                </div>
              </article>
          </div>
      </div>
   </div>
  );
}


const mapDispatchToProps = (dispatch) => ({
  toggleAddUser : () => dispatch(toggleAddUser()),
  getUserOptions: () => { dispatch(requestUserDropDownOptions())}
})

export default connect(null, mapDispatchToProps)(AddUser);
