import React, {useEffect} from 'react'
import './drop-down.styles.scss'
import { createStructuredSelector } from 'reselect';

// import useDropDownFetch from '../../effects/use-fetch-drop-down.effect';

import { connect } from "react-redux";
import { requestSelectedAssetData } from "../../redux/asset/asset.actions";
import { requestDropDownOptions } from '../../redux/drop-downs/drop-down.actions'

import  selectedListItemID  from "./drop-down.utils";
import { selectAssets } from "../../redux/drop-downs/drop-down.selectors";

const AssetDropDown =({ getAssetDetails, getAssetOptions, dropDownItems }) => {

    useEffect(() => {
        // Get Data from /allunits.
        getAssetOptions()
      }, [getAssetOptions]); //
    
    return(
        <div className='combo-box-container' >
            <select id="asset-list" className = 'combo-box' onChange={getAssetDetails} >
                <option text=' '/>
                {   
                    dropDownItems ? (
                        dropDownItems.map(cartItem => (
                    <option key={ cartItem.id }> { cartItem.id } </option> 
                    ))
                    ):(
                    <option></option>
                    )
                }
                
            </select>
        </div>
    )
}

const mapStateToProps = createStructuredSelector({
    dropDownItems: selectAssets
})


const mapDispatchToProps = (dispatch) => ({
    getAssetDetails: () => { dispatch(requestSelectedAssetData(selectedListItemID('asset-list')))},
    getAssetOptions: () => { dispatch(requestDropDownOptions())}
})

export default connect(mapStateToProps, mapDispatchToProps)(AssetDropDown);
