import React from 'react'
import './drop-down.styles.scss'

import useFetch from '../../effects/use-fetch.effect';

import { connect } from "react-redux";
import { requestSelectedUserData } from "../../redux/user/user.actions"



const UserDropDown =({ getUserDetails }) => {

useFetch(`http://localhost:3000/users`, 'user-list');

    return(
        <div className='combo-box-container'  >
            <select id="user-list" className = 'combo-box' onChange={ getUserDetails }>
                <option text=' '/>
            </select>
        </div>
    )
}

function selectedUserID() {
    var UserIdNode = document.getElementById('user-list');
    var value = UserIdNode.options[UserIdNode.selectedIndex].text;
    return value
};

const mapStateToProps = (state) => ({
    state:state
});

const mapDispatchToProps = (dispatch) => ({
    getUserDetails: () => { dispatch( requestSelectedUserData ( selectedUserID() ) )}
});

export default connect(mapStateToProps, mapDispatchToProps)(UserDropDown);
