import { useState, useEffect } from "react";

// pass in our url for the API call and the id of the html list that we want to target in our .jsx file. 
const useFetch = (url) => {
    let [data] = useState([]);

    useEffect(() => {
        const fetchReport = async () => {
            const res = await fetch(url);
            const dataArray = await res.json();
            return dataArray
        };
        
        console.log(fetchReport());
    }, [url] );
    console.log(data)
    return data;
};

export default useFetch;
