import React, {useEffect} from 'react'
import { createStructuredSelector } from 'reselect';

import { connect } from "react-redux";
import { requestSelectedUserData } from "../../redux/user/user.actions";
import { requestUserDropDownOptions } from '../../redux/drop-downs/drop-down.actions'

import  selectedListItemID  from "./drop-down.utils";
import { selectUsers } from "../../redux/drop-downs/drop-down.selectors";
import { currentMemberId } from "../../redux/site-member/site-member.selectors";

import { DropDownContainer, DropDown } from "./drop-down.styles";

const AssetDropDown =({ getUserDetails, getUserOptions, users, memberId }) => {

    useEffect(() => {
        // Get Data from /allusers when component mounts.
        getUserOptions(memberId)
      }, [getUserOptions, memberId]); // getUserOptions will not update, it was passed as a dependency to mute the console.warning. 
    
    return(
        <DropDownContainer>
            <DropDown id="user-list" onChange={getUserDetails} >
                <option text=' '/>
                {   
                    users ? (
                        users.map(user => (
                    <option key={ user.id }> { user.id } </option> 
                    ))
                    ):(
                    <option></option>
                    )
                }
            </DropDown>
        </DropDownContainer>
    )
}

const mapStateToProps = createStructuredSelector({
    memberId: currentMemberId,
    users: selectUsers
})

const mapDispatchToProps = (dispatch) => ({
    getUserDetails: () => { dispatch( requestSelectedUserData ( selectedListItemID('user-list') ) )},
    getUserOptions: (memberId) => { dispatch( requestUserDropDownOptions(memberId)) }
})

export default connect(mapStateToProps, mapDispatchToProps)(AssetDropDown);
