import React, { useState } from 'react'
import { withRouter } from 'react-router-dom';
import { connect } from "react-redux";

import { handleMemberSignin } from "../../redux/site-member/site-member.actions";

import './signin.styles.scss'

const SigninPage = ({ history, signIn }) => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const onEmailChange = (event) => {
        setEmail(event.target.value)
    };

    const onPasswordChange = (event) => {
        setPassword(event.target.value)
    };

    const onSubmitSignIn = () => {
        fetch('http://localhost:3000/signin', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                email: email,
                password: password
            })
        })
        .then(response => response.json()) 
        .then(member => {
            if (member.id) {
                signIn(member.id)
                history.push('/home')
            }
        })
    }
        
    return (
        <div className="signin-body">
            <article className="signin-container">
                <main className="signin-inner-container">
                    <div className="measure">
                        <fieldset id="sign_up" className="signin-fieldset">
                            <legend className="signin-legend">Sign In</legend>
                            
                            <div className="mt3">
                                <label className="singnin-label" htmlFor="email-address">Email</label>
                                <input
                                    className="signin-input" 
                                    type="text" 
                                    name="email-address"  
                                    id="email-address"
                                    onChange={onEmailChange} 
                                />
                            </div>

                            <div className="mv3">
                                <label className="singnin-label" htmlFor="password">Password</label>
                                <input  
                                    className="signin-input" 
                                    type="password" 
                                    name="password"  
                                    id="password"
                                    onChange={onPasswordChange} 
                                    />
                            </div>
                            
                        </fieldset>

                        <div className="">
                            <input 
                                onClick = {onSubmitSignIn}
                                className="signin-submit" 
                                type="submit" 
                                value="Sign in"
                            />
                        </div>
                            
                        <div className="register-container">
                            <p 
                                onClick = {() => history.push('/register')} 
                                className="register-link">Register
                            </p>
                        </div>
                    </div>
                </main>
            </article>
        </div>
        );
}


const mapStateToProps = (state) => ({
    state:state,
    isSignedIn: state.memberState.memberSignedIn
  });

const mapDispatchToProps = (dispatch) => ({
    signIn: (memberId) => {dispatch(handleMemberSignin(memberId))}
  });

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(SigninPage));
