import { checkOutSelectedAssetSaga } from "../../redux/asset/asset.sagas";
import { checkOutAsset } from "../../services/api";

function timeout(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
};
// Used to add a gap between updating the database and requesting the new asset details from the database
export const assetTransaction = async( transaction, fetchNew, assetID, userID=null) => {
    if (transaction === checkOutAsset) {
        console.log('checkout transaction')
    }
    try{
        await transaction(assetID, userID);
        await timeout(100);
        await fetchNew(assetID);
    } catch(e) {
        console.log(e)
    }
};
