import React from 'react';
import CustomButton from '../custom-button/custom-button.component';
import { connect } from "react-redux";
import { useAlert } from 'react-alert';

import base64ArrayBuffer from '../../utils/base64';
import { assetTransaction } from "./center.utils";
import image404 from '../../images/404.png';

import {
    checkInSelectedAssetStart, 
    requestSelectedAssetData, 
    checkOutSelectedAssetStart, 
    quarantineSelectedAssetStart 
} from "../../redux/asset/asset.actions";

import { CenterPanelContainer, CenterPanelInnerContainer, Header, AssetName, AssetSerial, ImageContainer, AssetImage, AssetStatus, ButtonContainer, Footer, Instructions, ReasonBox } from "./center.styles.jsx";
import './center.styles.scss';

const Center = ({
    assetID, assetName, assetSerial, 
    assetImage, assetStatus, checkInAsset, 
    getUpdatedStatus ,checkOutAsset, quarantineAsset, 
    userID, quarantineReason
}) => {

    const alert = useAlert();

    // prevent user from being able to drag asset images.
    const preventDragHandler = (event) => {
        event.preventDefault();
    };

    const onReasonChange = (event) => {
        quarantineReason = event.target.value;
    };


    const handleCheckout= () => {
        if ( assetStatus.slice(0,9) === 'In Use By' ){
            alert.show(`${assetName} is ${assetStatus}`,{type: 'info'})
        } else if (!userID){
            alert.show(`Username is required for checkout`,{type: 'info'})
            document.getElementById('user-list').focus() 
        } else {
            assetTransaction(checkOutAsset, getUpdatedStatus, assetID, userID)
            document.getElementById('asset-list').focus() 
        }
    };

    const handleQuarantine = () => {
        if (assetStatus === 'Quarantine' ){
            alert.show(`${assetName} is already in quarantine.`,{type: 'info'})
        } else if (!quarantineReason){
            alert.show(`Reason is required to check ${assetName} into quarantine.`,{type: 'info'})
            document.getElementById("text-area").focus()
        } else {
            assetTransaction(quarantineAsset, getUpdatedStatus, assetID, quarantineReason);
            document.getElementById('text-area').value = '';
        }
    };

    // var statusStyle = {
    //     value: 'foo',
    //     onChange: this.handleChange
    //   };

    // if (condition)
    // inputProps.disabled = true;





    return (
        <CenterPanelContainer>
        {assetID ? (
                <CenterPanelInnerContainer>
                    <Header>
                        <AssetName>{assetName.toUpperCase()}</AssetName>
                        <AssetSerial>{assetSerial.toUpperCase()}</AssetSerial>
                    </Header>
                    <ImageContainer>   
                    {/* Here we are using our base64ArrayBuffer function to convert our image array into a viewable picture */}
                        <AssetImage 
                            src={`data:image/png;base64,${base64ArrayBuffer(assetImage.data)}`}  
                            alt='' 
                            onDragStart={preventDragHandler}
                        /> 
                    </ImageContainer>
                    <AssetStatus 
                        // Ternary operator is being used to determine the font color for status, based on the String value of assetStatus
                        // className={`${assetStatus === 'Available' ? 'available' : assetStatus.slice(0,9) === 'In Use By' ? 'in-use' : 'quarantine' } asset-status`}
                        {...(assetStatus === 'Available' ? {isAvailable: 'isAvailable'} : {})}
                        >
                        {assetStatus.toUpperCase()}
                    </AssetStatus>
                    <ButtonContainer>
                        <CustomButton 
                            isCheckIn 
                            onClick={
                                () => {assetStatus !== 'Available' 
                                ? assetTransaction(checkInAsset, getUpdatedStatus, assetID) 
                                : alert.show(`${assetName} is already checked in.`,{type: 'info'})}}>
                            CHECK IN
                        </CustomButton>
                        <CustomButton isCheckOut onClick={handleCheckout}>CHECK OUT</CustomButton>
                    </ButtonContainer>
                    <Footer>
                        <Instructions>If there are issues with the asset, please fill out the form below and submit</Instructions>
                        <ReasonBox 
                            id="text-area" 
                            onChange={onReasonChange} 
                            placeholder={quarantineReason}
                        ></ReasonBox>
                        <CustomButton isSubmit onClick={handleQuarantine}>SUBMIT</CustomButton>
                    </Footer>
                </CenterPanelInnerContainer>
                ):(  
                <CenterPanelInnerContainer>
                    <Header>
                        <AssetName>ASSET NAME</AssetName>
                        <AssetSerial>SERIAL NUMBER</AssetSerial>
                    </Header>
                    <ImageContainer>   
                        <AssetImage src={image404}  alt='' onDragStart={preventDragHandler}></AssetImage> 
                    </ImageContainer>
                    <span className={`asset-status`}>ASSET STATUS</span>
                    <ButtonContainer></ButtonContainer>
                    <Footer></Footer>
                </CenterPanelInnerContainer>
                )}
        </CenterPanelContainer>
    );
};

const mapStateToProps = (state) => ({
    assetID: state.assetData.id,
    assetName: state.assetData.name,
    assetSerial: state.assetData.serial,
    assetImage: state.assetData.image,
    assetStatus:state.assetData.status,
    userID: state.userData.id,
    quarantineReason: state.assetData.comments
});

const mapDispatchToProps = (dispatch) => ({
    checkInAsset:       (assetID)                   => { dispatch(checkInSelectedAssetStart(assetID)) },
    checkOutAsset:      (assetID, userID)           => { dispatch(checkOutSelectedAssetStart(assetID, userID)) },
    quarantineAsset:    (assetID, quarantineReason) => { dispatch(quarantineSelectedAssetStart(assetID, quarantineReason)) },
    getUpdatedStatus:   (assetID)                   => { dispatch(requestSelectedAssetData(assetID)) }
});

export default connect(mapStateToProps, mapDispatchToProps)(Center);

