import React, {useEffect} from 'react'
import './drop-down.styles.scss'
import { createStructuredSelector } from 'reselect';

import { connect } from "react-redux";
import { requestSelectedAssetData } from "../../redux/asset/asset.actions";
import { requestAssetDropDownOptions } from '../../redux/drop-downs/drop-down.actions'

import  selectedListItemID  from "./drop-down.utils";
import { selectAssets } from "../../redux/drop-downs/drop-down.selectors";
import { currentMemberId } from "../../redux/site-member/site-member.selectors";

const AssetDropDown =({ getAssetDetails, getAssetOptions, assets, memberId }) => {

    useEffect(() => {
        // Get Data from /allunits.
        getAssetOptions(memberId)
      }, [getAssetOptions, memberId]); //
    
    return(
        <div className='combo-box-container' >
            <select id="asset-list" autofocus className = 'combo-box' onChange={getAssetDetails} >
                <option text=' '/>
                {   
                    assets ? (
                        assets.map(asset => (
                    <option key={ asset.id }> { asset.id } </option> 
                    ))
                    ):(
                    <option></option>
                    )
                }
            </select>

            
        </div>
    )
}

const mapStateToProps = createStructuredSelector({
    memberId: currentMemberId,
    assets: selectAssets
})

const mapDispatchToProps = (dispatch) => ({
    getAssetDetails: () => { dispatch(requestSelectedAssetData(selectedListItemID('asset-list')))},
    getAssetOptions: (memberId) => { dispatch(requestAssetDropDownOptions(memberId))}
})

export default connect(mapStateToProps, mapDispatchToProps)(AssetDropDown);
