import React from 'react'
import './drop-down.styles.scss'

import useFetch from '../../effects/use-fetch.effect';

import { connect } from "react-redux";
import { requestApiData } from "../../redux/asset/asset.actions";


const AssetDropDown =({ getAssetDetails, state }) => {


useFetch(`http://localhost:3000/allunits`, 'asset-list');

    return(
        <div className='combo-box-container'  >
            <button onClick = {getAssetDetails}> getAssetDetails </button>
            <button onClick={() => console.log(`${state.data}`)}>Console log data</button>
            {/* state.data[0].id */}
            <select id="asset-list" className = 'combo-box' onChange={getAssetDetails}>
                <option text=' '/>
            </select>
        </div>
    )
}


const selectedAssetName = () => {
    var AssetIdNode = document.getElementById('asset-list');
    var value =
    AssetIdNode.options[AssetIdNode.selectedIndex].text;
    return value
}

const mapStateToProps = (state) => ({
    state: state,
    // data: state.data
});

const mapDispatchToProps = (dispatch) => ({
    getAssetDetails: () => { dispatch(requestApiData(selectedAssetName())) }
});



// const mapDispatchToProps = dispatch =>
//   bindActionCreators({ requestApiData }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(AssetDropDown);
