// import React, { useEffect, useState } from 'react'
import CustomButton from '../custom-button/custom-button.component'
import { connect } from "react-redux";

import base64ArrayBuffer from '../../utils/base64'
import { assetTransaction } from "./center.utils";
import image404 from '../../images/404.png'

import { checkInSelectedAssetStart, requestSelectedAssetData, checkOutSelectedAssetStart, quarantineSelectedAssetStart } from "../../redux/asset/asset.actions";

import './center.styles.scss'

const Center = ({
    assetID, assetName, assetSerial, 
    assetImage, assetStatus, checkInAsset, 
    getUpdatedStatus ,checkOutAsset, quarantineAsset, 
    userID, quarantineReason
}) => {

    const onReasonChange = (event) => {
        quarantineReason = event.target.value
    }

    const preventDragHandler = (event) => {
        event.preventDefault();
      }

    const clearTextArea = () => {
        document.getElementById('text-area').value = ''
    }

    return (
        <div className='center-container'>
        
                <div className='center-inner-container'>
                    <div className='header'>
                        <header className='asset-name'>{assetID ? (assetName.toUpperCase()) : 'ASSET NAME'}</header>
                        <span className='asset-serial'>{assetSerial.toUpperCase()}</span>
                    </div>
                    <div className='img-container'>   
                    {/* Here we are using our base64ArrayBuffer function to convert our image array into a viewable picture */}
                        <img className='asset-image' src={`data:image/png;base64,${base64ArrayBuffer(assetImage.data)}`}  alt='' onDragStart={preventDragHandler} ></img> 
                    </div>
                    <p 
                    // Ternary operator is being used to determine the font color for status, based on the String value
                        className={`${assetStatus === 'Available' ? 'available' : assetStatus.slice(0,9) === 'In Use By' ? 'in-use' : 'quarantine' } asset-status`}>
                        {assetStatus.toUpperCase()}
                    </p>
                    <div className='button-container'>
                        <CustomButton 
                            isCheckIn 
                            onClick={
                                () => {assetStatus !== 'Available' 
                                ? assetTransaction(checkInAsset, getUpdatedStatus, assetID) 
                                : alert(`${assetName} is already checked in.`)}}>
                            CHECK IN
                        </CustomButton>
                        <CustomButton 
                            isCheckOut 
                            onClick={
                                () => {assetStatus.slice(0,9) !== 'In Use By' 
                                ? assetTransaction(checkOutAsset, getUpdatedStatus, assetID, userID) 
                                : alert(`${assetName} is ${assetStatus}.`)}} >
                            CHECK OUT
                        </CustomButton>
                    </div>
                    <div className='footer'>
                        <span className='instructions'>If there are issues with the asset, please fill out the form below and submit</span>
                        <textarea id="text-area" className='reason-box' onChange={onReasonChange} placeholder={quarantineReason}></textarea>
                        <CustomButton 
                            type='button' 
                            onClick={
                                () => {assetStatus !== 'Quarantine' 
                                ? assetTransaction(quarantineAsset, getUpdatedStatus, assetID, quarantineReason) && clearTextArea() 
                                : alert(`${assetName} is already in quarantine.`)}}>
                            SUBMIT
                        </CustomButton>
                    </div>
                </div>
        </div>
    )
}

const mapStateToProps = (state) => ({
    state: state,
    assetID: state.assetData.id,
    assetName: state.assetData.name,
    assetSerial: state.assetData.serial,
    assetImage: state.assetData.image,
    assetStatus:state.assetData.status,
    userID: state.userData.id,
    quarantineReason: state.assetData.comments
});

const mapDispatchToProps = (dispatch) => ({
    checkInAsset:       (assetID)                   => { dispatch(checkInSelectedAssetStart(assetID)) },
    checkOutAsset:      (assetID, userID)           => { dispatch(checkOutSelectedAssetStart(assetID, userID)) },
    quarantineAsset:    (assetID, quarantineReason) => { dispatch(quarantineSelectedAssetStart(assetID, quarantineReason)) },
    getUpdatedStatus:   (assetID)                   => { dispatch(requestSelectedAssetData(assetID)) }
})

export default connect(mapStateToProps, mapDispatchToProps)(Center);

