import { useState, useEffect } from "react";

// pass in our url for the API call and the id of the html list that we want to target in our .jsx file. 
const useFetch = (url) => {
    const [data] = useState(null);

    useEffect(() => {
        const fetchList = async () => {
            const res = await fetch(url);
            const dataArray = await res.json();
            // console.log(dataArray)
            return dataArray
        };
        
        fetchList();
    }, [url] );
    console.log(data)
    return data;
};

export default useFetch;
