import React from 'react';
import { connect } from 'react-redux';
import { checkInSelectedAssetStart } from "../../redux/asset/asset.actions";

import './custom-button.styles.scss';


const checkOut = () => {
    console.log("Checking Outa")
};


const CustomButton = ({children, isCheckIn, isCheckOut, state,getAssetDetails,assetID,checkInSelectedAssetStart, ...props}) => {
    return (
        <div>
            <button
                className={`${isCheckIn ? 'check-in' : isCheckOut ? 'check-out' : 'submit' }  custom-button`}
                onClick={isCheckIn ? getAssetDetails : isCheckOut ? checkOut : submitting} 
            >
                {children}
            </button>
            <button onClick={(assetID) => checkInSelectedAssetStart(assetID)} > TESTING THE ASSET </button>
        </div>
    )
}

const mapStateToProps = (state) => ({
    state: state,
    assetID: state.assetData.id,
    assetName: state.assetData.name,
    assetSerial: state.assetData.serial,
    assetImage: state.assetData.image,
    assetStatus:state.assetData.status,
});

const submitting = () => {
    console.log(Object)
};

const mapDispatchToProps = (dispatch) => ({
    getAssetDetails: () => { dispatch(checkInSelectedAssetStart())}
})

export default connect(mapStateToProps, mapDispatchToProps)(CustomButton);
