import styled, { css } from 'styled-components'

const inputField = css`
    padding: 0.5rem;
    -webkit-appearance: none;
    -moz-appearance: none;
    border-style: solid;
    border-width: 1px;
    width: 94%;
`

export const Header  = styled.h1`
    background-color: rgb(35, 38, 88);
    color: white;
    padding: 0.5em;
    text-align: center;
    margin-bottom: 0;
`;

export const ModalMain = styled.div`
    display: flex;
    justify-content: center;
    position: absolute;
    top: 0%;
    left: 0%;
    width: 100%;
    height: 100vh;
    background-color: rgba(0, 0, 0, 0.2);
    z-index: 1001;

    @media screen and (max-width: 1500px) {
        height: 157vh;
      }
`;

export const ModalContent = styled.div`
    position: absolute;
    top: 10%;
    width: 50%;
    height: auto;
    width: 450px;
    padding-bottom: 2em;
    background-color: white;
    z-index: 1002;
    overflow: auto;
    border-radius: 5px;
    border: 2px solid black;
`;

export const ModalReportContent  = styled.div`
    min-width: 450px;
    background-color: white;
    margin-top: 2em;
    margin-bottom: 2em;
    width: 65%;
    border-radius: 5px;
    border: 2px solid black;
    overflow: scroll;

    @media screen and (max-width: 1500px) {
        height: 90vh;
        width: 80%;
        margin-top: 2em;
        margin-bottom: 0;
      }
`;

export const SubModalMain  = styled.div`
    display: flex;
    justify-content: center;
    position: absolute;
    top: 0%;
    left: 0%;
    width: 100%;
    height: 100vh;
    background-color: rgba(0, 0, 0, 0.2);
    z-index: 1003;

    @media screen and (max-width: 1500px) {
        height: 157vh;
      }
`;

export const SubModalContent = styled.div`
