import React, { useState }  from "react";
import {connect} from 'react-redux';
import axios from 'axios';
import './popup.scss' 
import { toggleAddModel } from "../../redux/pop-ups/pop-ups.actions";

 const AddModel = ({ toggleAddModel }) => {

  let [model, setModel] = useState('');
  let [filename, setFilename] = useState(null);

  const onModelChange = (event) => {
    setModel(event.target.value);
  };

  const onFileChange = (event) => {
     setFilename(event.target.files[0]);
  };

  const onFileUpload = () => { 
    // Create an object of formData 
    const formData = new FormData(); 
    // Update the formData object 
    formData.append( 
      "myFile", 
      filename, 
      filename.name,
    ); 
    formData.append('model', model);
    
    // Request made to the backend api 
    // Send formData object 
    axios.post("http://localhost:3000/upload", formData )
   };
     
  const test = () => {
    console.log(filename)
    console.log(filename.name)
    console.log(model)
    console.log(FormData)
  }
  
  return (
   <div className="sub_modal_main">
      <div className="sub_modal_content">
      <span className="close" onClick={toggleAddModel}>&times;    </span>
          <div className='container pl1'>
              <h1>Add Model</h1>

              <button onClick={ () => test()}>TESTY</button>
              <article className=" center">
                <div action="sign-up_submit" method="get" acceptCharset="utf-8">
                  <fieldset className="ba b--transparent ph0 mh0">
                    <div>
                      <label className="db fw6 lh-copy" htmlFor="model">Model Name</label>
                      <input onChange={onModelChange} className="pa2 input-reset ba w-100" type="text" name="model"  id="model"/>
                    </div>

                    <div className='mt2'>
                      <label className="db fw6 lh-copy" htmlFor="image">Image</label>
                      <form className='flex flex-column'  encType="multipart/form-data" method="POST"> 
                        <input onChange={onFileChange} type="file" name="pic" className="pa2 input-reset ba"/>
                      </form>
                    </div>

                  </fieldset>
                  <div className="mt3">
                    <input 
                      onClick={onFileUpload} 
                      className="b ph3 pv2 input-reset ba b--black grow pointer f6" 
                      type = "Submit"
                      defaultValue="Submit"
                    />
                    </div>
                </div>
              </article>
          </div>
      </div>
   </div>
  );
}



const mapDispatchToProps = (dispatch) => ({
  toggleAddModel: () => dispatch(toggleAddModel())
});

export default connect(null, mapDispatchToProps)(AddModel);
