import React from 'react'
import './register.styles.scss'

class RegisterPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            fname: '',
            lname: '',
            email: '',
            password: ''
        }
    }

    
    onFirstNameChange = (event) => {
        this.setState({fname: event.target.value})
    }

    onLastNameChange = (event) => {
        this.setState({lname: event.target.value})
    }


    onEmailChange = (event) => {
        this.setState({email: event.target.value})
    }

    onPasswordChange = (event) => {
        this.setState({password: event.target.value})
    }

    onSubmitRegister = () => {
        fetch('http://localhost:3000/register', {
            method: 'post',
            // & Content-Type is in quotes because it has a hyphen in the name.
            headers: {'Content-Type': 'application/json'},
            // & the body needs to be parsed, becuase JSON cannot be read as raw input in browsers
            body: JSON.stringify({
                fname: this.state.fname,
                lname: this.state.lname,
                email: this.state.email,
                password: this.state.password
            })
        })
        // & Handling incorrect credentials
        .then(response => response.json()) 
        .then(member => {
            if (member) {
                // & this.props means it is a function that is built in the APP.js component
                this.props.loadMember(member)
                this.props.onRouteChange('home')
            }
        })
    }



    render() {    
        return (
        <article className="registration-container mv">
            <main className="registration-inner-container">
                <div className="measure">
                    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                    <legend className="f1 fw6 ph0 mh0">Register</legend>    



                    <div className="mt3 name-field-container">
                        <div className='name-field'>   
                            <label className="db fw6 lh-copy f6" htmlFor="name">First Name</label>
                            <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100 tc" 
                                type="text" 
                                name="fname"  
                                id="fname"
                                onChange ={this.onFirstNameChange }
                                />
                        </div>

                        <div className='name-field'>   
                            <label className="db fw6 lh-copy f6 lname" htmlFor="name">Last Name</label>
                            <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100 tc" 
                                type="text" 
                                name="lname"  
                                id="lname"
                                onChange ={this.onLastNameChange }
                            />
                        </div>
                    </div>


                    <div className="mt3">
                        <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
                        <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100 tc" 
                            type="email" 
                            name="email-address"  
                            id="email-address"
                            onChange = {this.onEmailChange}
                        />
                    </div>

                    <div className="mv3">
                        <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
                        <input className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100 tc" 
                            type="password" 
                            name="password"  
                            id="password"
                            onChange ={this.onPasswordChange}
                        />
                    </div>
                    
                    </fieldset>
                    <div className="">
                    <input className="pointer b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib tc" 
                        type="submit" 
                        value="Register"
                        onClick = {this.onSubmitRegister}
                        />
                    </div>
                </div>
            </main>
        </article>
    )}
}

export default RegisterPage
