function* gen(transaction, fetchNew, assetId, userId, ownerId, assetName, assetSerial, assetModel, assetComments){
        yield () => transaction(assetId, userId, ownerId, assetName, assetSerial, assetModel, assetComments);
        yield () => fetchNew(assetId);
};

export const assetTransaction = gen;
