import React from 'react';
import CustomButton from '../custom-button/custom-button.component';
import { connect } from "react-redux";
import { createStructuredSelector } from 'reselect';
import { assetId, assetName, assetSerial, assetModel, assetImage, assetComments,  assetOwnerId, assetStatus  } from "../../redux/asset/asset.selectors";

import { useAlert } from 'react-alert';
import { confirmAlert } from 'react-confirm-alert';

import { assetTransaction } from "./center.utils";
import robot from '../../images/robot3.jpg';

import {checkInSelectedAssetStart, requestSelectedAssetData, checkOutSelectedAssetStart, quarantineSelectedAssetStart} from "../../redux/asset/asset.actions";

import { removeAsset } from "../../services/api";

import { CenterPanelContainer, CenterPanelInnerContainer, Header, AssetName, AssetSerial, RemoveButton, ImageContainer, AssetImage, 
    AssetStatus, ButtonContainer, Footer, Instructions, ReasonBox } from "./center.styles.jsx";    
import 'react-confirm-alert/src/react-confirm-alert.css'; 

const Center = ({assetId, assetName, assetSerial,  assetImage, assetStatus, checkInAsset, getUpdatedStatus ,checkOutAsset, 
    quarantineAsset, userID, assetComments}) => {

    const alert = useAlert();

    const preventDragHandler = (event) => {
        event.preventDefault();
    };

    const onReasonChange = (event) => {
        assetComments = event.target.value;
    };

    const handleCheckout= () => {
        if ( assetStatus.slice(0,9) === 'In Use By' ){
            alert.show(`${assetName} is ${assetStatus}`,{type: 'info'})
        } else if (!userID){
            alert.show(`Username is required for checkout`,{type: 'info'})
            document.getElementById('user-list').focus() 
        } else {
            assetTransaction(checkOutAsset, getUpdatedStatus, assetId, userID)
            document.getElementById('asset-list').focus() 
        }
    };

    const handleQuarantine = () => {
        if (assetStatus === 'Quarantine' ){
            alert.show(`${assetName} is already in quarantine.`,{type: 'info'})
        } else if (!assetComments){
            alert.show(`Reason is required to check ${assetName} into quarantine.`,{type: 'info'})
            document.getElementById("text-area").focus()
        } else {
            assetTransaction(quarantineAsset, getUpdatedStatus, assetId, assetComments);
            document.getElementById('text-area').value = '';
        }
    };

    const handleRemoveAsset = () => {
        confirmAlert({
          title: 'Confirm Delete',
          message: `Are you sure to delete ${assetName.toUpperCase()} ?`,
          buttons: [
            {
              label: 'Yes',
              onClick: () => {removeAsset(assetId); alert.show(`${assetName} has been removed.`,{type: 'success'});}
            },
            {
              label: 'No',
              onClick: () => alert.show('Operation Cancelled')
            }
          ]
        });
      };

    return (
        <CenterPanelContainer>
        {assetId ? (
                <CenterPanelInnerContainer>

                    <Header>
                        <AssetName>{assetName.toUpperCase()}</AssetName>
                        <AssetSerial>{assetSerial.toUpperCase()}</AssetSerial>
                    </Header>

                    <ImageContainer>   
                        <RemoveButton onClick={handleRemoveAsset}>REMOVE ASSET</RemoveButton>
                        <AssetImage src={assetImage}  alt='' onDragStart={preventDragHandler}/>
                    </ImageContainer>
                    
                    <AssetStatus 
                        {...( assetStatus === 'Available' ? {isAvailable: true} 
                            : assetStatus.slice(0,9) === 'In Use By' ? {isInUse: true} 
                            : {isQurantined: true} )}
                    >
                        {assetStatus.toUpperCase()}
                    </AssetStatus>

                    <ButtonContainer>
                        <CustomButton 
                            isCheckIn 
                            onClick={
                                () => {assetStatus !== 'Available' 
                                ? assetTransaction(checkInAsset, getUpdatedStatus, assetId) 
                                : alert.show(`${assetName} is already checked in.`,{type: 'info'})}}>
                            CHECK IN
                        </CustomButton>
                        <CustomButton isCheckOut onClick={handleCheckout}>CHECK OUT</CustomButton>
                    </ButtonContainer>

                    <Footer>
                        <Instructions>If there are issues with the asset, please fill out the form below and submit</Instructions>
                        <ReasonBox id="text-area" onChange={onReasonChange} placeholder={assetComments}/>
                        <CustomButton isSubmit onClick={handleQuarantine}>SUBMIT</CustomButton>
                    </Footer>

                </CenterPanelInnerContainer>

                ):(  

                <CenterPanelInnerContainer>
                <AssetImage src={robot}  alt='' onDragStart={preventDragHandler}/>
                    <h2>PLEASE SELECT AN ASSET FROM THE LIST TO GET STARTED</h2>
                    <h3>IF THIS IS YOUR FIRST TIME, PLEASE ADD AN ASSET</h3>
                </CenterPanelInnerContainer>
                )}
        </CenterPanelContainer>
    );
};

const mapStateToProps = createStructuredSelector({
    assetId: assetId,
    assetName: assetName,
    assetSerial: assetSerial,
    assetImage: assetImage,
    assetStatus:assetStatus,
    userID: state.userData.id,
    assetComments: assetComments
});

const mapDispatchToProps = (dispatch) => ({
    checkInAsset:       (assetId)                   => { dispatch(checkInSelectedAssetStart(assetId)) },
    checkOutAsset:      (assetId, userID)           => { dispatch(checkOutSelectedAssetStart(assetId, userID)) },
    quarantineAsset:    (assetId, assetComments) => { dispatch(quarantineSelectedAssetStart(assetId, assetComments)) },
    getUpdatedStatus:   (assetId)                   => { dispatch(requestSelectedAssetData(assetId)) }
});

export default connect(mapStateToProps, mapDispatchToProps)(Center);

