import { 
  TOGGLE_AVAILABLE_UNITS,
  TOGGLE_QUARANTINED_UNITS ,
  TOGGLE_UNITS_IN_USE,
  TOGGLE_ADD_USER,
  TOGGLE_ADD_UNIT,
  TOGGLE_ADD_MODEL
} from "./pop-ups.actions";  
  
  const INITIAL_STATE = {
    seenAvailableUnits: false,
    seenUnitsInUse:false,
    seenQuarantinedUnits : false,
    seenAddUser: false,
    seenAddUnit: false,
    seenAddModel: false
  }
  
  // eslint-disable-next-line import/no-anonymous-default-export
  const popUpState = (state = INITIAL_STATE  , { type }) => {
    switch (type) {
      case TOGGLE_AVAILABLE_UNITS:
        return {
          ...state,
          seenAvailableUnits: !state.seenAvailableUnits
      };
      case TOGGLE_UNITS_IN_USE:
        return {
          ...state,
          seenUnitsInUse: !state.seenUnitsInUse
      };
      case TOGGLE_QUARANTINED_UNITS:
        return {
          ...state,
          seenQuarantinedUnits: !state.seenQuarantinedUnits
      };
      case TOGGLE_ADD_USER:
        return {
          ...state,
          seenAddUser: !state.seenAddUser
      };
      case TOGGLE_ADD_UNIT:
        return {
          ...state,
          seenAddUnit: !state.seenAddUnit
      };
      case TOGGLE_ADD_MODEL:
        return {
          ...state,
          seenAddModel: !state.seenAddModel
      };
      default:
        return state;
    }
  };
  
  export default popUpState;
