import React, {useEffect} from 'react'
import './drop-down.styles.scss'
import { createStructuredSelector } from 'reselect';

import { connect } from "react-redux";
import { requestSelectedUserData } from "../../redux/user/user.actions";
import { requestUserDropDownOptions } from '../../redux/drop-downs/drop-down.actions'

import  selectedListItemID  from "./drop-down.utils";
import { selectUsers } from "../../redux/drop-downs/drop-down.selectors";

const AssetDropDown =({ getUserDetails, getUserOptions, users }) => {

    useEffect(() => {
        // Get Data from /allusers when component mounts.
        getUserOptions()
      }, [getUserOptions]); // getUserOptions will not update, it was passed as a dependency to mute the console.warning. 
    
    return(
        <div className='combo-box-container' >
            <select id="user-list" className = 'combo-box' onChange={getUserDetails} >
                <option text=' '/>
                {   
                    users ? (
                        users.map(user => (
                    <option key={ user.id }> { user.id } </option> 
                    ))
                    ):(
                    <option></option>
                    )
                }
            </select>
        </div>
    )
}

const mapStateToProps = createStructuredSelector({
    users: selectUsers
})

const mapDispatchToProps = (dispatch) => ({
    getUserDetails: () => { dispatch( requestSelectedUserData ( selectedListItemID('user-list') ) )},
    getUserOptions: () => { dispatch( requestUserDropDownOptions()) }
})

export default connect(mapStateToProps, mapDispatchToProps)(AssetDropDown);
