import React, { useState } from "react";
import {connect} from 'react-redux';
import './popup.scss' 
import AddModel from './add-model.popup'


  // id: '',
  // name: '',
  // model: '',
  // serial: '',
  // models: [],
  // seenAddModel: false



import { toggleAddUnit } from "../../redux/pop-ups/pop-ups.actions";

const AddUnit = ( {toggleAddUnit}) => {


  // useEffect(() => {
  //   fetch('http://localhost:3000/models', {
  //     method: 'get',
  //     headers: {'Content-Type': 'application/json'},
  //    })
  //    .then(data => data.json())
  //    .then( data => {
  //    for (var i = 0; i < data.length; i++) {
  //      var opt = data[i];
  //      var el = document.createElement("option");
  //      el.textContent = opt.title;
  //      el.value = i;
  //      el.dataset.model = opt.title;
  //      document.getElementById("combo").append(el);
  //    }
  //    document.getElementById("combo").addEventListener('change', function() {
  //      selectedModel= this.options[this.selectedIndex].dataset.model;
  //    });
  //   return () => {
  //     cleanup
  //   }
  // }, [])

  let [assetID, setAssetID] = useState('')
  let [assetName, setAssetName] = useState('')
  let [assetModel, setAssetModel] = useState('')
  let [assetSerial, setAssetSerial] = useState('')

  const onAssetidChange = (event) => {
    setAssetID(event.target.value); 
  } ;

  const onAssetNameChange = (event) => {
    setAssetName(event.target.value);
  };

  let selectedModel = ''

  const onAssetModelChange = (event) => {
    setAssetModel(selectedModel);
  };

 const onAssetSerialChange = (event) => {
    setAssetSerial(event.target.value);
  };

  return (
    
   <div className="modal_main ">
     <div className="modal_content ">
     <span className="close" onClick={toggleAddUnit}>&times;</span>
        <div className='container'>
            <h1>Add Unit</h1>
            <article className="center">
              <div action="sign-up_submit" method="get" acceptCharset="utf-8">
                <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                  <div>
                    <label className="db fw6 lh-copy" htmlFor="unitid">Unit ID</label>
                    <input onChange={onAssetidChange} className="pa2 input-reset ba" type="text" name="unitid"  id="unitid"/>
                  </div>
                  <div className="mt3">
                    <label className="db fw6 lh-copy" htmlFor="password">Unit Name</label>
                    <input onChange={onAssetNameChange} className="pa2 input-reset ba " type="text" name="unitname"  id="unitname"/>
                  </div>
                  
                  <div className="mt3 ">
                    <label className="db fw6 lh-copy" htmlFor="model">Model</label>
                      <select id='combo' onChange={onAssetModelChange} className="pa2 input-reset ba " type="text" name="model">
                        <option>Select A Model</option>
                      </select> 
                      <p id='addmodelbtn' onClick={this.seenAddModel} className='pointer'>Add New Model</p>
                  </div>
                  
                  <div className="mt3">
                    <label className="db fw6 lh-copy" htmlFor="serial">Serial Number</label>
                    <input onChange={onAssetSerialChange} className="pa2 input-reset ba " type="text" name="serial"  id="serial"/>
                  </div>
                </fieldset>
                <div className="mt3">
                  <input 
                    onClick={this.onSubmitUnit} 
                    className="b ph3 pv2 input-reset ba b--black grow pointer f6" 
                    type = "Submit"
                    defaultValue="Submit"
                  />
                  </div>
              </div>
            </article>
        </div>
    </div>
    {/* {this.state.seenAddModel ? <AddModel toggle={this.seenAddModel} /> : null} */}
   </div>
  );
}

const mapDispatchToProps = (dispatch) => ({
    toggleAddUnit: () => dispatch(toggleAddUnit())
  })

export default connect(null, mapDispatchToProps)(AddUnit);
