export const fetchSelectedAssetData = async(assetID) => {
    const settings = { 
        method:'POST',
        headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            id:assetID
        })
    };
    try{
        const response = await fetch(`http://localhost:3000/asset`, settings);
        console.log(`Response is: ${response}`)
        const asset = await response.json();
        console.log(`Asset is: ${asset.id}`)
        return asset
    } catch(error) {
        console.log(error);
    }
};


