// import React, { useEffect, useState } from 'react'
import CustomButton from '../custom-button/custom-button.component'
import { connect } from "react-redux";
import './center.styles.scss'

import base64ArrayBuffer from '../../utils/base64'

const Center = ({ assetID, assetName, assetSerial, assetImage, assetStatus }) => {

    return (
        <div className='center-container'>
        {assetID ? (
                <div className='center-inner-container'>
                    <div className='header'>
                        <header className='asset-name'>{assetName}</header>
                        <span className='serial'>{assetSerial}</span>
                    </div>
                    {/* <h1>{base64ArrayBuffer(assetImage)}</h1> */}
                    <img src={`data:image/png;base64,${assetImage}`}></img>
                    <h3>{assetStatus}</h3>
                    <div className='button-container'>
                        <CustomButton isCheckIn>CHECK IN</CustomButton>
                        <CustomButton isCheckOut  >CHECK OUT</CustomButton>
                    </div>
                    <div className='footer'>
                        <span className='instructions'></span>
                        <textarea className='reason-box' defaultValue={JSON.stringify(assetImage)}></textarea>
                        <CustomButton>Submit</CustomButton>
                    </div>
                </div>
                ):(  
                    <div className='center-inner-container'>
                        <h1>Nothing to display</h1>
                    </div>
                )}
        </div>
    )
}

const mapStateToProps = (state) => ({
    state: state,
    assetID: state.data.id,
    assetName: state.data.name,
    assetSerial: state.data.serial,
    assetImage: state.data.image,
    assetStatus:state.data.status,
});


export default connect(mapStateToProps)(Center);


