import React from 'react'
import './drop-down.styles.scss'

import useFetch from '../../effects/use-fetch.effect';


function selectedAssetName() {
    const AssetIdNode = document.getElementById('asset-list');
    const value =
    AssetIdNode.options[AssetIdNode.selectedIndex].text;
    const bitchnigga = value;
    return bitchnigga
}

const AssetDropDown =({bitchnigga}) => {

useFetch(`http://localhost:3000/allunits`, 'asset-list');

    return(
        <div className='combo-box-container'  >
        
            {/* <input  
                className = 'combo-box'
                type="text" 
                list="asset-list"
                /> */}
            <select id="asset-list" className = 'combo-box' onChange={() => {selectedAssetName()}}>
                <option text=' '/>
            </select>
            
        </div>
    )
}

export default AssetDropDown;
