import React from 'react'
import './drop-down.styles.scss'

// import useDropDownFetch from '../../effects/use-fetch-drop-down.effect';

import { connect } from "react-redux";
import { requestSelectedAssetData } from "../../redux/asset/asset.actions";
import { requestDropDownOptions, receiveDropDownOptions } from '../../redux/drop-downs/drop-down.actions'

import  selectedListItemID  from "./drop-down.utils";

const AssetDropDown =({ getAssetDetails, getAssetOptions, assetDropDown }) => {

    // useDropDownFetch(`http://localhost:3000/allunits`, 'asset-list');

    const loadOptions = (stateObject) => {
        const input = document.getElementById(`asset-list`)
    
        for (var i = 0; i < stateObject.length; i++) {
            var option = document.createElement("option");
            option.value = stateObject[i].id;
            option.text = stateObject[i].id;
            input.appendChild(option);
        }
    };

    return(
        <div className='combo-box-container'  >
        <button onClick={() => console.log(assetDropDown.length)}> console log DDS</button>
        <button onClick={() => loadOptions(assetDropDown)}> Load options</button>
            <select id="asset-list" className = 'combo-box' onChange={getAssetDetails} onClick={() => {getAssetOptions(); loadOptions(assetDropDown)}}>
                <option text=' '/>
            </select>
        </div>
    )
}

const mapStateToProps = (state) => ({
    state: state,
    assetDropDown: state.FUCK.assetDropDown
})

const mapDispatchToProps = (dispatch) => ({
    getAssetDetails: () => { dispatch(requestSelectedAssetData(selectedListItemID('asset-list')))},
    getAssetOptions: () => { dispatch(requestDropDownOptions())}
})

export default connect(mapStateToProps, mapDispatchToProps)(AssetDropDown);
