import React, { useEffect, useState } from 'react'
import CustomButton from '../custom-button/custom-button.component'
import './center.styles.scss'

const Center = ({ assetId }) => {
    const [asset, setAsset] = useState(null);
    
    useEffect(() => {
        const fetchAsset = async() => {
            const response = await fetch(`http://localhost:3000/asset`)
            const asset = await response.json()
            setAsset(asset);
        }

        fetchAsset()
    })

    return (
        <div className='center-container'>
            <div className='center-inner-container'>
                <div className='header'>
                    <header className='asset-name'>{`${asset} is the asset`}</header>
                    <span className='serial'>Serial Number Span</span>
                </div>
                <h1>PICTURE PLACE HOLDER</h1>
                <div className='button-container'>
                    <CustomButton isCheckIn >CHECK IN</CustomButton>
                    <CustomButton isCheckOut >CHECK OUT</CustomButton>
                </div>
                <div className='footer'>
                    <span className='instructions'>Instructions for Reason Span</span>
                    <textarea className='reason-box' defaultValue='Please write a brief explination'></textarea>
                    <CustomButton>Submit</CustomButton>
                </div>
            </div>
        </div>
    )
}

export default Center
