import React, { useState } from "react";
import { connect } from "react-redux";
import DatePicker from "react-datepicker";

import { toggleActvityTracking } from "../../redux/modal/modal.actions";

import { ModalMain, ModalContent, CloseButton, Header, Article,
  FieldSet, Label, Submit, ModalDate
} from "./modal.styles";

import AssetDropDown from '../custom-drop-down/asset-drop-down.component';
import UserDropDown from "../custom-drop-down/user-drop-down.component";


import "react-datepicker/dist/react-datepicker.css";

const ActivityTracking = ( {toggleActvityTracking, memberId} ) =>{

  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());

  let [userID, setUserID] = useState('');
  let [assetID, setFname] = useState('');

  const onUseridChange = (event) => {
    setUserID(event.target.value); 
  } ;

  const onFnameChange = (event) => {
    setFname(event.target.value);
  };


  // const inputReset = () => {
  //   userID = null;
  //   asset-id = null;
  //   user-id = null;
  //   document.getElementById('userid').value = '';
  //   document.getElementById('asset-id').value = '';
  //   document.getElementById('user-id').value = '';
  //   document.getElementById('userid').focus();
  // };

  // const fetchReport = (event) => {
  //   console.log(Date.parse(startDate))
  //   console.log(Date.parse(endDate))
  // };
  


  return (
   <ModalMain>
      <ModalContent>
        {/* Retrieve updated list of users when modal is closed. */}
        <CloseButton onClick={() => {toggleActvityTracking()}}>&times;</CloseButton>
        <Header>Activity Tracking</Header>
        <Article>
          <div action="sign-up_submit" method="get" acceptCharset="utf-8">
            <FieldSet id="sign_up">
                <Label htmlFor="email-address">Start Date</Label>
                <ModalDate 
                  selected={startDate} 
                  timeInputLabel="Time:"
                  dateFormat="MM/dd/yyyy h:mm aa"
                  showTimeInput
                  onChange={date => setStartDate(date)} 
                />
                <Label htmlFor="email-address">End Date</Label>
                <ModalDate
                  selected={endDate}       
                  timeInputLabel="Time:"
                  dateFormat="MM/dd/yyyy h:mm aa"
                  showTimeInput onChange={date => setEndDate(date)} 
                />
                <Label htmlFor="asset-id">Asset ID</Label>
                <AssetDropDown onChange={onFnameChange} type="text" name="asset-id"  id="asset-id" />
                <Label htmlFor="user-id">User ID</Label>
                <UserDropDown onChange={data => setLname(data)} type="text" name="user-id"  id="user-id"/>
            </FieldSet>
            <Submit onClick={() => fetchReport()} type = "submit" defaultValue="Submit"/>
          </div>
        </Article>
      </ModalContent>
   </ModalMain>
  );
};

const mapStateToProps = (state) => ({
  memberId: state.memberState.memberId
});

const mapDispatchToProps = (dispatch) => ({
  toggleActvityTracking : () => dispatch(toggleActvityTracking())
});

export default connect(mapStateToProps, mapDispatchToProps)(ActivityTracking);
