import { 
    RECEIVE_ASSET_DROP_DOWN_OPTIONS,
    RECEIVE_USER_DROP_DOWN_OPTIONS
    // ADD_ASSET
  } from "./drop-down.actions";
  
  const INITIAL_STATE = {
  }
  
  // eslint-disable-next-line import/no-anonymous-default-export
  const dropDownOptions = (state = INITIAL_STATE  , { type, assetDropDown, userDropDown }) => {
    switch (type) {
      case RECEIVE_ASSET_DROP_DOWN_OPTIONS:
        return {assetDropDown}
      case RECEIVE_USER_DROP_DOWN_OPTIONS:
        return {userDropDown}

      // case ADD_ASSET:
      //   return {
      //     ...state,
      //     assetDropDown: [...state.assetDropDown, ADD_ASSET.payload ]
      //   }
      default:
        return state;
    }
  };
  
  export default dropDownOptions;
