import React, { Component } from "react";
import './popup.scss' 

export default class AddUser extends Component {
  handleClick = () => {
   this.props.toggle();
  };
  constructor(props) {
    super(props);
    this.state = {
        userid: '',
        fname: '',
        lname: ''
    }
  }

  onSubmitUser = () => {
    fetch('http://localhost:3000/adduser', {
        method: 'post',
        headers: {'Content-Type': 'application/json'},
        body : JSON.stringify({
          userid: this.state.userid,
          fname: this.state.fname,
          lname: this.state.lname
        })
    })
    .then(data => data.json())
    document.getElementById('userid').value = ''
    document.getElementById('fname').value = ''
    document.getElementById('lname').value = ''
  }

  onUseridChange = (event) => {
    this.setState({userid: event.target.value}); 
  } ;

  onFnameChange = (event) => {
    this.setState({fname: event.target.value});
  };

  onLnameChange = (event) => {
    this.setState({lname: event.target.value});
  };

render() {
  return (
   <div className="modal_main">
      <div className="modal_content">
        <span className="close" onClick={this.handleClick}>&times;    </span>
          <div className='container'>
              <h1>Add User</h1>


              <article className="center">
                <div action="sign-up_submit" method="get" acceptCharset="utf-8">
                  <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                    <div>
                      <label className="db fw6 lh-copy" htmlFor="email-address">User ID</label>
                      <input onChange={this.onUseridChange} className="pa2 input-reset ba" type="text" name="userid"  id="userid"/>
                    </div>
                    <div className="mt3">
                      <label className="db fw6 lh-copy" htmlFor="password">First Name</label>
                      <input onChange={this.onFnameChange} className="pa2 input-reset ba " type="text" name="fname"  id="fname"/>
                    </div>
                    <div className="mt3">
                      <label className="db fw6 lh-copy" htmlFor="password">Last Name</label>
                      <input onChange={this.onLnameChange} className="pa2 input-reset ba " type="text" name="lname"  id="lname"/>
                    </div>
                  </fieldset>
                  <div className="mt3">
                    <input 
                      onClick={this.onSubmitUser} 
                      className="b ph3 pv2 input-reset ba b--black grow pointer f6" 
                      type = "Submit"
                      defaultValue="Submit"
                    />
                    </div>
                </div>
              </article>
          </div>
      </div>
   </div>
  );
 }
}
