// Used to add a gap between updating the database and requesting the new asset details from the database
const timeout = (ms) => {
    return new Promise(resolve => setTimeout(resolve, ms));
};

export const assetTransaction = async( transaction, fetchNew, assetID, userID = null, quarantineReason, assetName, assetSerial, mname, assetComments) => {
    try{
        await transaction(assetID, userID, quarantineReason, assetName, assetSerial, mname, assetComments);
        await timeout(100);
        await fetchNew(assetID);
    } catch(error) {
        console.log(error)
    }
};
