import React, { useState, useEffect } from "react";
import {connect} from 'react-redux';
import './popup.scss' 
import AddModel from './add-model.popup'

  // seenAddModel: false
import { toggleAddUnit } from "../../redux/pop-ups/pop-ups.actions";
import { addNewAsset } from "../../redux/asset/asset.actions";

const AddUnit = ( {toggleAddUnit, createNewAsset}) => {

// const [asset, setAsset] = useState(null);

// useEffect(() => {
//     const fetchAsset = async() => {
//         const response = await fetch(`http://localhost:3000/asset`)
//         const asset = await response.json()
//         setAsset(asset[0]);
//     }

//     fetchAsset();
// }, []);

const [models, setModels] = useState([]);

  useEffect(() => {
      const fetchModels = async() => {
      const response = await fetch('http://localhost:3000/models') 
      const models = await response.json()
      setModels(models[0])
      console.log(models)
    }

    fetchModels();
  }, []);


  //    .then(data => data.json())
  //    .then( data => {
  const test = () => {
    console.log('populate',typeof models[0])
     for (var i = 0; i < models.length; i++) {
       var opt = models[i];
       var el = document.createElement("option");
       el.textContent = opt.title;
       el.value = i;
       el.dataset.model = opt.title;
       document.getElementById("asset-model").append(el);
     }
     console.log('done')
    }
  //    document.getElementById("asset-model").addEventListener('change', function() {
  //      selectedModel= this.options[this.selectedIndex].dataset.model;
  //    });
  //   return () => {
  //     cleanup
  //   }
  // }, [])

  // useEffect(() => {
  //   fetch('http://localhost:3000/models', {
  //     method: 'get',
  //     headers: {'Content-Type': 'application/json'},
  //    })
  //    .then(data => data.json())
  //    .then( data => {
  //    for (var i = 0; i < data.length; i++) {
  //      var opt = data[i];
  //      var el = document.createElement("option");
  //      el.textContent = opt.title;
  //      el.value = i;
  //      el.dataset.model = opt.title;
  //      document.getElementById("asset-model").append(el);
  //    }
  //    document.getElementById("combo").addEventListener('change', function() {
  //      selectedModel= this.options[this.selectedIndex].dataset.model;
  //    });
  //   return () => {
  //     cleanup
  //   }
  // }, [])

  let [assetID, setAssetID] = useState('')
  let [assetName, setAssetName] = useState('')
  let [assetModel, setAssetModel] = useState('')
  let [assetSerial, setAssetSerial] = useState('')

  const onAssetidChange = (event) => {
    setAssetID(event.target.value); 
  } ;

  const onAssetNameChange = (event) => {
    setAssetName(event.target.value);
  };

  let selectedModel = ''

  const onAssetModelChange = (event) => {
    setAssetModel(selectedModel);
  };

 const onAssetSerialChange = (event) => {
    setAssetSerial(event.target.value);
  };

  const inputReset = () => {
    document.getElementById('asset-id').value = ''
    document.getElementById('asset-name').value = ''
    document.getElementById('asset-serial').value = ''
    document.getElementById('asset-id').focus()
    alert('Unit Created!')
  }

  return (
    
   <div className="modal_main ">
     <div className="modal_content ">
     <span className="close" onClick={toggleAddUnit}>&times;</span>
        <div className='container'>
            <h1>Add Unit</h1>
            <button onClick={() => test()}>Models</button>
            <article className="center">
              <div action="sign-up_submit" method="get" acceptCharset="utf-8">
                <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                  <div>
                    <label className="db fw6 lh-copy" htmlFor="asset-id">Unit ID</label>
                    <input onChange={onAssetidChange} className="pa2 input-reset ba" type="text" name="asset-id"  id="asset-id"/>
                  </div>
                  <div className="mt3">
                    <label className="db fw6 lh-copy" htmlFor="asset-name">Unit Name</label>
                    <input onChange={onAssetNameChange} className="pa2 input-reset ba " type="text" name="asset-name"  id="asset-name"/>
                  </div>
                  
                  <div className="mt3 ">
                    <label className="db fw6 lh-copy" htmlFor="asset-model">Model</label>
                      <select id='asset-model' onChange={onAssetModelChange} className="pa2 input-reset ba " type="text" name="asset-model">
                        <option>Select A Model</option>
                      </select> 
                      <p id='addmodelbtn' onClick={() => console.log('this.seenAddModel')} className='pointer'>Add New Model</p>
                  </div>
                  
                  <div className="mt3">
                    <label className="db fw6 lh-copy" htmlFor="asset-serial">Serial Number</label>
                    <input onChange={onAssetSerialChange} className="pa2 input-reset ba " type="text" name="asset-serial"  id="asset-serial"/>
                  </div>
                </fieldset>
                <div className="mt3">
                  <input 
                    onClick={() => {createNewAsset(assetID, assetName, assetModel, assetSerial); inputReset()}} 
                    className="b ph3 pv2 input-reset ba b--black grow pointer f6" 
                    type = "Submit"
                    defaultValue="Submit"
                  />
                  </div>
              </div>
            </article>
        </div>
    </div>
    {/* {this.state.seenAddModel ? <AddModel toggle={this.seenAddModel} /> : null} */}
   </div>
  );
}

const mapDispatchToProps = (dispatch) => ({
    createNewAsset: (assetID, assetName, assetModel, assetSerial) => { dispatch(addNewAsset(assetID, assetName, assetModel, assetSerial)) },
    toggleAddUnit: () => dispatch(toggleAddUnit())
  })

export default connect(null, mapDispatchToProps)(AddUnit);
