import React, {useEffect, useState} from "react";
import axios from "axios";
import { connect } from "react-redux";
import { toggleAvailableUnits } from "../../redux/pop-ups/pop-ups.actions";
import './popup.scss'


const AvailableUnits = ({ toggleAvailableUnits }) => {

    // const [data, setData] = useState();
 
    // useEffect(() => {
    //     const fetchData = async () => {
    //       const result = await axios(
    //           'http://localhost:3000/availableunits',
    //       );
     
    //       setData(result.data);
    //     };
     
    //     fetchData();
    //   }, []);

  return (
   <div className="modal_main">
        <div className="modal_report_content">
            <span className="close" onClick={toggleAvailableUnits}>&times;    </span>
            <div className='container'>
                <h1>Available Units</h1>
                <div className=" pb4 ">
                    <div className="overflow-auto">
                    {data ?
                        <table className="f6 w-100 mw8 tc" cellSpacing="0">
                        <thead>
                            <tr className="stripe-dark" >
                                <th className="fw6  pa3 bg-white">Unit ID</th>
                                <th className="fw6  pa3 bg-white">Unit Name</th>
                                <th className="fw6  pa3 bg-white">Model</th>
                                <th className="fw6  pa3 bg-white">Serial</th> 
                            </tr>
                        </thead>
                        
                        <tbody className="lh-copy">
                          
                               {data.map(unit => (
                                <tr key={unit.id} className="stripe-dark">
                                <td>{unit.id}</td>
                                <td >{unit.name}</td>
                                <td >{unit.model}</td>
                                <td >{unit.serial}</td>
                                </tr>
                            ))
                            }
                        </tbody>
                        
                        </table>
                        : "Loading..."}
                    </div>
                </div>
            </div>
        </div>
   </div>
  );
}

const mapStateToProps = (state) => ({
    seenAvailableUnits: state.popUpState.seenAvailableUnits
});

const mapDispatchToProps = dispatch => ({
    toggleAvailableUnits: () => dispatch(toggleAvailableUnits())
  });


export default connect(mapStateToProps, mapDispatchToProps)(AvailableUnits);
