import { 
    RECEIVE_DROP_DOWN_OPTIONS,
    // ADD_ASSET
  } from "./drop-down.actions";
  
  const INITIAL_STATE = {
    assetDropDown: []
  }
  
  // eslint-disable-next-line import/no-anonymous-default-export
  const assetDropDown = (state = INITIAL_STATE  , { type, assetDropDown }) => {
    switch (type) {
      case RECEIVE_DROP_DOWN_OPTIONS:
        return {...state, assetDropDown}

      // case ADD_ASSET:
      //   return {
      //     ...state,
      //     assetDropDown: [...state.assetDropDown, ADD_ASSET.payload ]
      //   }
      default:
        return state;
    }
  };
  
  export default assetDropDown;
