import React, { useState }  from "react";
import {connect} from 'react-redux';
import { useAlert } from 'react-alert';

import axios from 'axios';
import { toggleAddModel } from "../../redux/pop-ups/pop-ups.actions";
import { requestModelDropDownOptions } from '../../redux/drop-downs/drop-down.actions';

import { SubModalMain, SubModalContent, CloseButton, Header, Article,
        FieldSet, Label, Input, Submit
} from "./modal.styles";

 const AddModel = ({ toggleAddModel, getModelOptions, memberId }) => { 

  const alert = useAlert();

  let [model, setModel] = useState('');
  let [filename, setFilename] = useState(null);

  const onModelChange = (event) => {
    setModel(event.target.value);
  };

  const onFileChange = (event) => {
     setFilename(event.target.files[0]);
  };

  const inputReset = () => {
    model = null;
    filename = null;
    document.getElementById('model').value = '';
    document.getElementById('file').value = '';
  };

  const onFileUpload = () => { 
    if (model && filename && memberId){
        // Create an object of formData 
        const formData = new FormData(); 
        // Update the formData object 
        formData.append( 
          "myFile", 
          filename, 
          filename.name,
        ); 
        formData.append('model', model);
        formData.append('memberId', memberId);
        // Request made to the backend api 
        // Send formData object 
        axios.post("http://localhost:3000/upload", formData )
        alert.show(`${model} submitted`, {type: 'success', position:'top center'});
        inputReset();
    } else {
      alert.show('Please ensure that both fields contain information.', {type: 'error', position:'top center'});
    }
   };
     
  return (
   <SubModalMain>
      <SubModalContent>
        {/* Retrieve updated list of models when modal is closed. */}
      <CloseButton onClick={() => {toggleAddModel(); getModelOptions(memberId);} }>&times;</CloseButton>
          <div className='container pl1'>
              <Header>Add Model</Header>
              <Article>
                <div action="sign-up_submit" method="get" acceptCharset="utf-8">
                  <FieldSet>
                    <div>
                      <Label htmlFor="model">Model Name</Label>
                      <Input id="model" type="text" name="model" onChange={onModelChange}/>
                    </div>

                    <div className='mt2'>
                      <Label htmlFor="image">Image</Label>
                      <form className='flex flex-column' encType="multipart/form-data" method="POST"> 
                        <Input id="file" type="file" name="pic" onChange={onFileChange}/>
                      </form>
                    </div>

                  </FieldSet>
                  <div className="mt3">
                    <Submit 
                      type = "Submit"
                      defaultValue="Submit"
                      onClick={onFileUpload} 
                    />
                    </div>
                </div>
              </Article>
          </div>
      </SubModalContent>
   </SubModalMain>
  );
};

const mapStateToProps = (state) => ({
  memberId: state.memberState.memberId,
});

const mapDispatchToProps = (dispatch) => ({
  toggleAddModel: () => dispatch(toggleAddModel()),
  getModelOptions : (memberId) => { dispatch(requestModelDropDownOptions(memberId))}
});

export default connect(mapStateToProps, mapDispatchToProps)(AddModel);
