import { fetchAllAssetsForDropDown } from "../../services/api";


import { 
    RECEIVE_DROP_DOWN_OPTIONS,
    ADD_ASSET
  } from "./drop-down.actions";
  
  const INITIAL_STATE = {
    assetDropDown: fetchAllAssetsForDropDown()
  }
  
  // eslint-disable-next-line import/no-anonymous-default-export
  const dropDownState = (state = INITIAL_STATE  , { type, dropDownState }) => {
    switch (type) {
      case RECEIVE_DROP_DOWN_OPTIONS:
        return dropDownState;

      case ADD_ASSET:
        return {
          ...state,
          assetDropDown: [...state.assetDropDown, ADD_ASSET.payload ]
        }
      default:
        return state;
    }
  };
  
  export default dropDownState;
