import React, { Component } from 'react';
import Center from '../../components/center/center.component';
import ScanboxContainer from '../../components/left/left.component'
import ReportsContainer from '../../components/right/right.component';
import AvailableUnits from "../../components/pop-ups/available-units.popup";
import './homepage.styles.scss'
class Homepage extends Component {

    constructor(props) {
        super(props);
        this.state = {  
            seenAvailableUnits: false,
            seenUnitsInUse:false,
            seenAddUser: false,
            seenAddUnit: false,
            seenQuarantine : false
        }
      }

    componentDidMount() {
        console.log('Homepage Mounted')
    };

    availableUnitsReport = () => {
        this.setState({
         seenAvailableUnits: !this.state.seenAvailableUnits
        })
    };

    render() {
        const {seenAvailableUnits, availableUnitsReport} = this.state
        return (
            <div className='homepage-container'>
                <ScanboxContainer></ScanboxContainer>
                <Center></Center>
                <ReportsContainer ></ReportsContainer>
                {/* Toggle the Action Panel PopUps */}
                {seenAvailableUnits ? <AvailableUnits toggle={availableUnitsReport} /> : null}
                {/* {seenUnitsInUse ? <UnitsInUse toggle={unitsInUseReport} /> : null}
                {seenAddUser ? <AddUser toggle={addUser} /> : null}
                {seenAddUnit ? <AddUnit toggle={addUnit} /> : null}
                {seenQuarantine ? <QuarantinedUnits toggle={quarantineReport} /> : null} */}
            </div>
        );
    }
};

export default Homepage;
