import React            from 'react';
import { connect }      from 'react-redux';
import Center           from '../../components/center/center.component';
import ScanboxContainer from '../../components/left/left.component'
import ReportsContainer from '../../components/right/right.component';
import AvailableUnits   from '../../components/pop-ups/available-units.popup';
import QuarantinedUnits from '../../components/pop-ups/quarantined-units.popup';
import UnitsInUse       from '../../components/pop-ups/units-in-use.popup';
import AddUser          from '../../components/pop-ups/add-user.popup';
import AddUnit          from '../../components/pop-ups/add-unit.popup';
import { useDropDownFetch } from "../../effects/use-fetch-drop-down.effect";

import './homepage.styles.scss'

const Homepage = ({seenAvailableUnits, seenUnitsInUse, seenQuarantinedUnits, seenAddUser, seenAddUnit}) => {

    useDropDownFetch(`http://localhost:3000/allunits`, 'asset-list');

    return (
        <div className='homepage-container'>
            <ScanboxContainer></ScanboxContainer>
            <Center></Center>
            <ReportsContainer ></ReportsContainer>
            {/* Toggle the Action Panel PopUps */}
            {seenAvailableUnits   ? <AvailableUnits/>   : null}
            {seenUnitsInUse       ? <UnitsInUse/>       : null}
            {seenQuarantinedUnits ? <QuarantinedUnits/> : null}
            {seenAddUser          ? <AddUser/>          : null}
            {seenAddUnit          ? <AddUnit/>          : null}
        </div>
    );
};

const mapStateToProps = (state) => ({
    seenAvailableUnits  : state.popUpState.seenAvailableUnits,
    seenQuarantinedUnits: state.popUpState.seenQuarantinedUnits,
    seenUnitsInUse      : state.popUpState.seenUnitsInUse,
    seenAddUser         : state.popUpState.seenAddUser,
    seenAddUnit         : state.popUpState.seenAddUnit
})

export default connect(mapStateToProps)(Homepage);
