import React from 'react'
import './drop-down.styles.scss'

import useFetchDropDown from '../../effects/use-fetch-drop-down.effect';

import { firstAsset } from '../../redux/asset/asset.actions'
import { connect } from "react-redux";


function selectedAssetName() {
    var AssetIdNode = document.getElementById('asset-list');
    var value =
    AssetIdNode.options[AssetIdNode.selectedIndex].text;
    console.log(value);
}

const AssetDropDown =({ objState, assetFromReducer, myFunction }) => {

    useFetchDropDown(`http://localhost:3000/allunits`, 'asset-list');

    return(
        <div className='combo-box-container'  >
        <button onClick={myFunction} >TEST from asset drop down</button>
            <select id="asset-list" className = 'combo-box' onChange={() => {selectedAssetName(); console.log(objState);} }>
                <option text=' '/>
            </select>
            
        </div>
    )
}

const mapStateToProps = (state) => ({
    objState: state,
    assetFromReducer: state.asset
});

const mapDispatchToProps = (dispatch) => ({
    myFunction: () => { dispatch(firstAsset()) }
})

export default connect(mapStateToProps, mapDispatchToProps)(AssetDropDown);
