import { call, put, takeLatest, all, takeEvery } from "redux-saga/effects";
import { 
    REQUEST_SELECTED_USER,
    receiveSelectedUserData,
    ADD_NEW_USER,
    addNewUser
  } from "../user/user.actions";
import { fetchSelectedUserData, createNewUser } from '../../services/api';

function* getUserData(action) {
    try{
        const userData = yield call(fetchSelectedUserData, action.userID);
        yield put (receiveSelectedUserData(userData));
    } catch(error) {
        console.log(error);
    }
};

export function* requestSelectedUserSaga() {
    yield takeLatest( REQUEST_SELECTED_USER, getUserData ) ;
};

function* getNewUserData(action) {
    try{
        const userData = yield call(createNewUser, action.userId, action.fname, action.lname);
        yield put (addNewUser(userData));
    } catch(error) {
        console.log(error);
    }
};

export function* addNewUserSaga() {
    yield takeEvery( ADD_NEW_USER, getNewUserData ) ;
};


export function* userSagas() {
    yield all ( [ 
        call(requestSelectedUserSaga) ,
        call(addNewUserSaga)
    ] )
};
